#METHODS

#Methods 6 - Size order

	#Methods 1 - Size order

	def size_order(journalists)
		journalists = journalists.sort_by(&:length)
	end

order = size_order(journalists)


#Method 3 - Alphabetical order

	def alphabet_order(journalists)
		journalists.sort_by { |journalists| journalists }
	end
alphabet = alphabet_order (journalists)


#Method 3 - Count handles of 5 char

	def search_fives(journalists)
		journalists.find_all{|journalists| journalists.length == 6}
	end

all_fives = search_fives (journalists)

	def count_fives(all_fives)
		all_fives.count
	end

total_fives = count_fives(all_fives)


#Method 4 - Capital = first letter

	def capital_first(journalists)
	i = 0
		381.times do |a|
			if journalists[a][1] == journalists[a][1].capitalize
			i = i + 1
			end 
		end
	return i 
	end

capitals = capital_first(journalists)


#Method 7: 

	def find_element(journalists)
		p journalists.find_index('@epenser')
	end
epenser = find_element(journalists)


